changeSet.createProcedure:
  CREATE PROCEDURE DataValidation.InsertTicket  
   @TicketId varchar(36),
   @IsAvailable char(1),
   @FacilityId int,
   @TicketTypeId int,
   @InitiatedOn datetime
  AS 
 BEGIN

  IF EXISTS( SELECT Id FROM DataValidation.Tickets WHERE Id = @TicketId )
     UPDATE 
      DataValidation.Tickets
     SET 
      FacilityId = @FacilityId, 
      TicketTypeId = @TicketTypeId
     WHERE 
      Id = @TicketId
  ELSE 
     INSERT INTO
      DataValidation.Tickets
     (
        FacilityId, 
        Id, 
        ResultsAreAvailable, 
        InitiatedOnDate, 
        TicketTypeId
     )
     VALUES 
     (
        @FacilityId, 
        @TicketId, 
        @IsAvailable, 
        @InitiatedOn, 
        @TicketTypeId
     )
END;
