changeSet.createProcedure:
CREATE PROCEDURE Facility.SelectAllFacilities AS BEGIN
SELECT Id, HospitalCode, Name, ServerId, DatabaseName, ModType, FollowupUnitId, UtcOffset, DstOffset, OrderComm, TenetCare, Reregister, TaxId AS FederalTaxID, UseUscMrn AS UsesUscMrn, MedicaidIssuedDateRequired FROM Facility.Facilities WHERE IsEnabled = 'Y' ORDER BY Name
END;
