changeSet.createProcedure:
CREATE PROCEDURE Logging.SaveLogEntry
  @Host VARCHAR(255) = 'Unknown',
  @Date DATETIME = NULL,
  @Thread VARCHAR(255) = 'Unknown',
  @Level VARCHAR(50) = 'Unknown',
  @Logger VARCHAR(255) = 'Unknown',
  @Message VARCHAR(max),
  @SourceLocation VARCHAR(512) = 'Unknown',
  @Exception VARCHAR(max) = NULL
AS
BEGIN

  INSERT INTO
    Logging.Events
  ( Host,
    Date,
    Thread,
    Level,
    Logger,
    Message,
    SourceLocation,
    Exception )
  VALUES
  ( @Host,
    @Date,
    @Thread,
    @Level,
    @Logger,
    @Message,
    @SourceLocation,
    @Exception )

END;
