changeSet.createProcedure:
CREATE PROCEDURE RuleEngine.SelectActionsWorklistMappings AS BEGIN
SELECT Actions.Id AS ActionId, Worklists.Id AS WorklistId, Worklists.Name AS WorklistName FROM RuleEngine.Actions LEFT OUTER JOIN RuleEngine.RulesToActions ON RulesToActions.ActionId = Actions.Id LEFT OUTER JOIN Worklist.WorklistsToRules ON RulesToActions.RuleId = WorklistsToRules.RuleId LEFT OUTER JOIN Worklist.Worklists ON WorklistsToRules.WorklistId = Worklists.Id ORDER BY RulesToActions.ActionId
END;
