changeSet.createProcedure:
CREATE PROCEDURE RuleEngine.SelectAllActions AS BEGIN
SELECT Actions.Id as ActionId, Actions.Type, Actions.Description, CompositeActionsToActions.CompositeActionId, RuleEngine.IsCompositeAction( Actions.Id ) AS IsComposite FROM RuleEngine.Actions LEFT OUTER JOIN RuleEngine.CompositeActionsToActions ON Actions.Id = CompositeActionsToActions.ActionId ORDER BY Actions.Id
END;
