changeSet.createProcedure:
CREATE PROCEDURE RuleEngine.SelectAllRules AS BEGIN
SELECT DISTINCT RuleEngine.IsCompositeRule( Rules.Id ) AS IsComposite, Rules.Id as RuleId, Rules.Type, Rules.Description, Rules.Severity, CompositeRules.Id AS CompositeRuleId, CompositeRules.Type AS CompositeRuleType FROM RuleEngine.Rules LEFT OUTER JOIN RuleEngine.CompositeRulesToRules ON Rules.Id = CompositeRulesToRules.RuleId LEFT OUTER JOIN RuleEngine.CompositeRules ON CompositeRulesToRules.RuleId = RuleEngine.CompositeRules.Id ORDER BY RuleEngine.Rules.Type
END;
