changeSet.createProcedure:
CREATE PROCEDURE RuleEngine.SelectRuleActionMappings AS BEGIN
SELECT RulesToActions.RuleId, Actions.Id as ActionId, Actions.Type as Type, Actions.Description as Description, CompositeActionsToActions.CompositeActionId as CompositeActionId, RuleEngine.IsCompositeAction( Actions.Id ) as IsComposite FROM RuleEngine.Actions LEFT OUTER JOIN RuleEngine.CompositeActionsToActions ON RuleEngine.CompositeActionsToActions.ActionId = Actions.Id JOIN RuleEngine.RulesToActions ON Actions.Id = RulesToActions.ActionId ORDER BY RulesToActions.RuleId
END;
