changeSet.createProcedure:
CREATE PROCEDURE Worklist.SaveWorklistSettings
  @UserID int,
  @StartingLetters varchar(15) = NULL,
  @EndingLetters varchar(15) = NULL,
  @StartDate datetime = NULL,
  @EndDate datetime = NULL,
  @WorklistID int,
  @RangeTypeID int,
  @SortedColumn int = NULL,
  @SortedColumnDirection int = NULL
AS
BEGIN

  DELETE FROM
    Worklist.Settings
  WHERE
    UserId = @UserID
  AND
    WorklistId = @WorklistID

  INSERT Worklist.Settings(
    StartLetters,
    EndLetters,
    SelectionRangeId,
    WorklistId,
    UserId,
    StartDate,
    EndDate,
    SortedColumn,
    SortedColumnDirection)
  VALUES(
    @StartingLetters,
    @EndingLetters,
    @RangeTypeId,
    @WorklistId,
    @UserId,
    @StartDate,
    @EndDate,
    @SortedColumn,
    @SortedColumnDirection)

END;
