changeSet.createProcedure:
CREATE PROCEDURE Worklist.SelectRangesByWorklist @WorklistId int = NULL AS BEGIN
SELECT WorklistsToSelectionRanges.WorklistId, SelectionRanges.Id as SelectionRangeId, SelectionRanges.Description, SelectionRanges.RangeInDays FROM Worklist.WorklistsToSelectionRanges, Worklist.SelectionRanges WHERE Worklist.SelectionRanges.Id = WorklistsToSelectionRanges.SelectionRangeId AND ( WorklistsToSelectionRanges.WorklistId = @WorklistId OR @WorklistId IS NULL ) ORDER BY WorklistsToSelectionRanges.WorklistId, SelectionRanges.DisplayOrder
END;
