changeSet.createProcedure:
CREATE PROCEDURE Worklist.SelectRangesForAllWorklists AS BEGIN
SELECT WorklistsToSelectionRanges.WorklistId, SelectionRanges.Id as SelectionRangeId, SelectionRanges.Description, SelectionRanges.RangeInDays FROM Worklist.WorklistsToSelectionRanges, Worklist.SelectionRanges WHERE Worklist.SelectionRanges.Id = WorklistsToSelectionRanges.SelectionRangeId ORDER BY WorklistsToSelectionRanges.WorklistId, SelectionRanges.DisplayOrder
END;
